function stringTogether(x){
var str = ""
for (var i=0; i<x.length; i++){
var xi = x[i]; if (xi[0] == "(" || xi[1] == "-") {xi = xi[0] + xi[1].toUpperCase() + xi.substring(2)}
str = str + " " + xi
}
return str.substring(1)
}


function rp(x){
if (typeof x == "number") {var r = recipeList[x]}
else {var r = x}
var ingredients = r["ingredients"].split("<>").map(x => x.split(" ").map(ph => ph[0].toUpperCase() + ph.substring(1))).map(x => "---" + stringTogether(x) + "---").toString().replace("---","[\"").replaceAll("---,---","\"],[\"").replace("---","\"]")
var steps = "\"" + r["steps"].replaceAll("||","\",\"") + "\""

var array = []
array.push(r["name"]); array.push(r["cat"]); array.push(r["time"]);
array.push(ingredients); array.push(steps)

return array
}



function createOldRecipe(x,p){
var div = document.createElement("div"); div.style = "background-color: black; color: white; padding: 5vw; margin-bottom: 20px; width: 95%;"
  for (var i=0; i<x.length; i++){
var el = document.createElement("input"); el.style = "padding-left: 20px; margin-bottom: 20px; width: 95%; height: auto;"; el.value = x[i]; div.appendChild(el);
el.onclick = function(event){cp(event)}
}

p.appendChild(div)
}

function cOR(start,end){
var parent = document.getElementById("mainList"); var pl = parent.childNodes.length;
for (var i=0; i<pl; i++) {parent.childNodes[0].remove()}

if (start == undefined) {var s = 0} else {var s = start}
if (end == undefined) {var e = recipeList.length} else {var e = end}
for (var i=s; i<e; i++){  createOldRecipe(rp(i),parent) }}

function cp(event) {
  var e = event.composedPath()[0]
  e.select();
  navigator.clipboard.writeText(e.value);
}
